mapper.select:
SELECT 

FROM contract_receivable a

WHERE a.id = #{id}

SELECT 

FROM contract_receivable a




		AND a.bill_no = #{billNo}


		AND a.bill_date = #{billDate}


		AND a.custom_abbr = #{customAbbr}


		AND a.month_amount = #{monthAmount}


		AND a.contract_amount = #{contractAmount}


		AND a.check_status = #{checkStatus}


		AND a.create_person = #{createPerson}


		AND a.check_person = #{checkPerson}


		AND a.make_date = #{makeDate}


		AND a.check_date = #{checkDate}


		AND a.contract_remark = #{contractRemark}


		AND a.ext_field1 = #{extField1}


		AND a.ext_field2 = #{extField2}


		AND a.ext_field3 = #{extField3}


		AND a.remark = #{remark}




		ORDER BY ${page.orderBy}
