mapper.select:
SELECT 

FROM doc_car a

WHERE a.id = #{id}

SELECT 

FROM doc_car a




		AND a.car_code LIKE
		'%'||#{carCode}||'%'
		'%'+#{carCode}+'%'
		CONCAT('%', #{carCode}, '%')


		AND a.car_no  LIKE
		'%'||#{carNo}||'%'
		'%'+#{carNo}+'%'
		CONCAT('%', #{carNo}, '%')


		AND a.driver_name = #{driverName}


		AND a.car_owner = #{carOwner}


		AND a.car_licence = #{carLicence}


		AND a.card_no = #{cardNo}


		AND a.car_typename = #{carTypename}


		AND a.empty_weight = #{emptyWeight}


		AND a.drive_cube = #{driveCube}


		AND a.car_phone = #{carPhone}


		AND a.is_mixing = #{isMixing}


		AND a.is_pump = #{isPump}


		AND a.is_other = #{isOther}


		AND a.is_avalable = #{isAvalable}


		AND a.drive_record = #{driveRecord}




		ORDER BY ${page.orderBy}

SELECT 

FROM doc_car a






		ORDER BY ${page.orderBy}
